/*!50003 CREATE DEFINER=`zhxm`@`` FUNCTION `f_ripspe_str`(
		`in_str` varchar(512),
		`in_data` double(22 ,4),
		`in_bhl` double(22 ,4),
		`in_bhlv` double(22 ,4),
		`in_xjwc` double(22 ,4),
		`in_cz` double(22 ,4),
		`in_czc` double(22 ,4)
) RETURNS varchar(512) CHARSET utf8
BEGIN
	#Routine body goes here...
	DECLARE result VARCHAR(400);
	if(in_str is null or in_str='') then return null;
	ELSE 
	  set  result = replace(replace( replace(replace(in_str,'{',''),'}',''),'[',''),']','');
	  set result = replace(result,':dqz',ifnull(in_data,''));
    set result = replace(result,':bhlv',ifnull(in_bhlv,''));
    set result = replace(result,':bhl',ifnull(in_bhl,''));
    set result = replace(result,':xjwc',ifnull(in_xjwc,''));
    set result = replace(result,':czcjdz',ifnull(in_czc,''));
    set result = replace(result,':czc',ifnull(in_czc,''));
    set result = replace(result,':cz',ifnull(in_cz,''));
    set result = replace(result,'%','/100');
 
