  DECLARE v_bhl double(22,4); -- 变化量
  DECLARE v_czc double(22,4) DEFAULT 0; -- 初值差
  DECLARE v_bhlv double(22,4); -- 变化率

	DECLARE v_bdz , v_device_type , v_device ,v_canshu  int;
	DECLARE v_time datetime;
	DECLARE v_data , v_datald double(22,4);
	DECLARE flag varchar(64);

	DECLARE v_sum int DEFAULT 0;
	DECLARE i int DEFAULT 0;



	DECLARE cur  cursor for(		
			-- device_type_id=4油色谱数据处理周期1天，未在tb_sjcl_clpl_lx中配置的数据默认也为一天）
			SELECT bdz_id,device_type_id,device_id,MEASURETAG,rtime,data,lastData from (
			SELECT a.bdz_id,a.device_type_id,a.device_id,MEASURETAG,a.rtime ,a.AVG_DATA data,
			case when @content = CONCAT(BDZ_ID,device_type_id,Device_id,MEASURETAG ) then @lastData else NULL end lastData,
			@lastData := AVG_DATA,
			@content := CONCAT(BDZ_ID,device_type_id,Device_id,MEASURETAG )
			from measuredata.tf_data_his a,(SELECT @lastData:='',@content:='')b where rtime BETWEEN DATE_ADD(CURDATE(),INTERVAL -5 day) and DATE_ADD(CURDATE(),INTERVAL -1 day) and MEASURETAG != 1
			ORDER BY BDZ_ID,device_type_id,Device_id,MEASURETAG,rtime asc) a where rtime BETWEEN DATE_ADD(CURDATE(),INTERVAL -3 day) and DATE_ADD(CURDATE(),INTERVAL -1 day)
	 );

 DECLARE CONTINUE HANDLER for SQLEXCEPTION set flag=0;

		-- 得到总数，循环使用
			SELECT count(1) into  v_sum from (
			SELECT a.bdz_id,a.device_type_id,a.device_id,MEASURETAG,a.rtime ,a.AVG_DATA data,
			case when @content = CONCAT(BDZ_ID,device_type_id,Device_id,MEASURETAG ) then @lastData else NULL end lastData,
			@lastData := AVG_DATA,
			@content := CONCAT(BDZ_ID,device_type_id,Device_id,MEASURETAG )
			from measuredata.tf_data_his a,(SELECT @lastData:='',@content:='')b where rtime BETWEEN DATE_ADD(CURDATE(),INTERVAL -5 day) and DATE_ADD(CURDATE(),INTERVAL -1 day) and MEASURETAG != 1
			ORDER BY BDZ_ID,device_type_id,Device_id,MEASURETAG,rtime asc) a where rtime BETWEEN DATE_ADD(CURDATE(),INTERVAL -3 day) and DATE_ADD(CURDATE(),INTERVAL -1 day);



		OPEN cur;
		while(i < v_sum) Do
			set i = i + 1 ;
			FETCH cur into v_bdz,v_device_type,v_device,v_canshu,v_time,v_data,v_datald ;

			set flag = 0;

			-- 设置默认值
			set v_czc = 0;
			set v_chuzhi = 0;
			set v_sql = 'a';
			
			-- 🎹🎹🎹🎹🎹🎹🎹🎹🎹🎹 后期处理 🎹🎹🎹🎹🎹🎹🎹🎹🎹🎹
			if(v_device_type = '8' and v_device in (11,12,13,14,15,16)) then SET flag ='0'; else
			
				#flag 赋值相当于return结束不执行下面内容
				if(v_data is null) then  set flag= '4';
						else
								if(v_datald is null or v_datald <= -12345 or v_datald = '') then set v_bhl = 0; set v_bhlv = 0; else

								 set v_bhl = (ifnull(v_data,0)-ifnull(v_datald,0)) ;
									select case v_datald when 0 then 1 else v_bhl/v_datald end  into v_bhlv from dual; 
								end if;

								select count(1) into v_count from config.tf_thmt_mb mb,tf_thmt_mb_detail detail WHERE mb.MB_ID = detail.MB_ID and mb.device_type_id =v_device_type and detail.measureTag =v_canshu;
							
								if(v_count=0) then set flag= '0';	
								else 
										select ifnull(sfcz,0) into v_sfcz from tf_thmt_chuzhi_flag where device_Type_id = v_device_type and measureTag = v_canshu;
										
										if(v_sfcz=1) then 
											set v_chuzhi = f_getchuzhi(v_device_type,v_device,v_canshu);
											if(v_chuzhi is null) then set flag= '0';
											else 								
												if(round(v_chuzhi,2)=0) then set flag= '0';
												else 
														set v_czc = abs(v_chuzhi-v_data)/v_chuzhi;
												--  0810
												select concat("select (case when not ",ifnull(f_ripspe_str(yx,v_data,v_bhl,v_bhlv,v_xjwc,v_chuzhi,v_czc),'1=1')," then "'4'"
																 when ",ifnull(f_ripspe_str(gj,v_data,v_bhl,v_bhlv,v_xjwc,v_chuzhi,v_czc),'1=2')," then "'1'"
																 when ",ifnull(f_ripspe_str(bj,v_data,v_bhl,v_bhlv,v_xjwc,v_chuzhi,v_czc),'1=2')," then "'2'"
																 when ",ifnull(f_ripspe_str(zy,v_data,v_bhl,v_bhlv,v_xjwc,v_chuzhi,v_czc),'1=2')," then "'3'"
																 when ",ifnull(f_ripspe_str(zc,v_data,v_bhl,v_bhlv,v_xjwc,v_chuzhi,v_czc),'1=2')," then "'0'"
																 else "'0'" end)   into @jgss   ")  into v_sql from  v_thmt_all where substation_Id = v_bdz and dev_type_id =v_device_type and device_id = v_device and measureTag =v_canshu;		

												call f_excute_sql(v_sql,flag);	
																				
												end if;
												
											end if;
										else 

													select concat("select (case when not ",ifnull(f_ripspe_str(yx,v_data,v_bhl,v_bhlv,v_xjwc,v_chuzhi,v_czc),"1=1")," then "'4'"
																 when ",ifnull(f_ripspe_str(yjgj,v_data,v_bhl,v_bhlv,v_xjwc,v_chuzhi,v_czc),'1=2')," then "'1'"
																 when ",ifnull(f_ripspe_str(bj,v_data,v_bhl,v_bhlv,v_xjwc,v_chuzhi,v_czc),'1=2')," then "'2'"
																 when ",ifnull(f_ripspe_str(zy,v_data,v_bhl,v_bhlv,v_xjwc,v_chuzhi,v_czc),'1=2')," then "'3'"
																 when ",ifnull(f_ripspe_str(zc,v_data,v_bhl,v_bhlv,v_xjwc,v_chuzhi,v_czc),'1=2')," then "'0'"
																 else "'0'" end)  into @jgss  ")  into v_sql from  v_thmt_all where substation_Id = v_bdz and dev_type_id =v_device_type and device_id = v_device and measureTag =v_canshu;												
												
														 call f_excute_sql(v_sql,flag);	
											
										end if;

								end if;

				end if; 
			end if;
			select flag,v_bdz,v_device_type,v_device,v_canshu;
		  update measuredata.tf_data_his set DATA_FLAG=case when device_type_id not in(13,4) and flag ='4' then 0 else  flag end where bdz_id=v_bdz and device_type_id=v_device_type and Device_id=v_device and MEASURETAG= v_canshu and rtime = v_time;
		END WHILE;
		close cur;


END */$$
DELIMITER ;

/* Procedure structure for procedure `p_sjcl_lssjnh` */

/*!50003 DROP PROCEDURE IF EXISTS  `p_sjcl_lssjnh` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`zhxm`@`` PROCEDURE `p_sjcl_lssjnh`()
BEGIN

				-- 异常捕获，异常捕获到，回滚事务，错误信息插入日志表
			DECLARE err_state varchar(100);
			DECLARE err_msg  varchar(5000) ;
			DECLARE EXIT HANDLER for SQLEXCEPTION 
			BEGIN
				ROLLBACK;
				GET DIAGNOSTICS CONDITION 1 
				err_state = RETURNed_SQLSTATE,err_msg = message_text;
				-- insert into T_log_trig(trig_name,s_time,e_time,status,err_msg) VALUES('p_sjcl_lssjnh',@stime,SYSDATE(),'0',CONCAT('[',err_state,']',err_msg));
			end;
		-- 开始事务
		   START TRANSACTION;

		   set @stime = SYSDATE();



		delete from measuredata.me_ycl_temp;

		-- 筛选me_ycl表中近三天数据存入缓存表
		
		-- 取me_ycl_temp近两天数据 取max,avg,min值,然后新增或修改tf_data_his表
		update measuredata.tf_data_his a join
		(
				select a.device_id device_id,a.dev_type_id,ycl.MEASURETAG MEASURETAG,DATE_ADD(CURDATE(),INTERVAL -2 day) rtime,a.substation_Id bdzId,
							 round(avg(ycl.value),5) avg_data,
							 round(min(ycl.value),5) min_data,
							 round(max(ycl.value),5) max_data 
				from (select point.valid_flag,dev.substation_Id,dev.CLASS_NAME dev_type_id,dev.id device_id,point.dev_id locate_zyid,b.measuretag from cisp_dev.v_all_dev dev,cisp_platform.me_conf_measurepoint point,cisp_platform.me_conf_measure b  where dev.id = point.END_DEV_ID and point.id = b.MEASUREPOINT_ID) a,
				measuredata.me_ycl_temp ycl
				where ycl.MEASURETAG = a.MEASURETAG and ycl.DEV_ID = a.locate_zyid 
							and ycl.SAMPLETIME >= DATE_ADD(CURDATE(),INTERVAL -2 day) and ycl.SAMPLETIME < DATE_ADD(CURDATE(),INTERVAL -1 day)
							and (ycl.status <> 17 or ycl.status is null)
							and ycl.MEASURETAG <>1 and ycl.value>-12345 and ycl.value is not null
							and a.valid_flag = 1
				GROUP BY MEASURETAG,bdzId,device_id,a.dev_type_id
				UNION ALL
				select a.device_id device_id,a.dev_type_id,ycl.MEASURETAG MEASURETAG,DATE_ADD(CURDATE(),INTERVAL -2 day) rtime,a.substation_Id bdzId,
						 round(avg(ycl.value),5) avg_data,
						 round(min(ycl.value),5) min_data,
						 round(max(ycl.value),5) max_data 
				from (select point.valid_flag,dev.substation_Id,dev.CLASS_NAME dev_type_id,dev.id device_id,point.dev_id locate_zyid,b.measuretag from cisp_dev.v_all_dev dev,cisp_platform.me_conf_measurepoint point,cisp_platform.me_conf_measure b  where dev.id = point.END_DEV_ID and point.id = b.MEASUREPOINT_ID) a,
				measuredata.me_ycl_temp ycl
				where ycl.MEASURETAG = a.MEASURETAG  and ycl.DEV_ID = a.locate_zyid 
						 and ycl.SAMPLETIME >= DATE_ADD(CURDATE(),INTERVAL -1 day) and ycl.SAMPLETIME < CURDATE()
						 and (ycl.status <> 17 or ycl.status is null)
						 and ycl.MEASURETAG <>1 and ycl.value>-12345 and ycl.value is not null
						 and a.valid_flag = 1
				GROUP BY MEASURETAG,bdzId,device_id,a.dev_type_id
		) tj
		on a.bdz_id=tj.bdzId and a.Device_id=tj.device_id and a.MEASURETAG=tj.MEASURETAG and a.rtime=tj.rtime
		set a.avg_data=tj.avg_data,a.min_data=tj.min_data,a.max_data=tj.max_data;
		
		select tj.* from (
				select a.device_id device_id,a.dev_type_id,ycl.MEASURETAG MEASURETAG,DATE_ADD(CURDATE(),INTERVAL -2 day) rtime,a.substation_Id bdzId,
							 round(avg(ycl.value),5) avg_data,
							 round(min(ycl.value),5) min_data,
							 round(max(ycl.value),5) max_data 
				from (select point.valid_flag,dev.substation_Id,dev.class_name dev_type_id,dev.id device_id,point.dev_id locate_zyid,b.measuretag from cisp_dev.v_all_dev dev,cisp_platform.me_conf_measurepoint point,cisp_platform.me_conf_measure b  where dev.id = point.END_DEV_ID and point.id = b.MEASUREPOINT_ID) a,
				measuredata.me_ycl_temp ycl
				where ycl.MEASURETAG = a.MEASURETAG-- and ycl.DEV_ID = a.locate_zyid 
							and ycl.SAMPLETIME >= DATE_ADD(CURDATE(),INTERVAL -2 day) and ycl.SAMPLETIME < DATE_ADD(CURDATE(),INTERVAL -1 day)
							and (ycl.status <> 17 or ycl.status is null)
							and ycl.MEASURETAG <>1 and ycl.value>-12345 and ycl.value is not null
							and a.valid_flag = 1
				GROUP BY MEASURETAG,bdzId,device_id,a.dev_type_id
				UNION ALL
				select a.device_id device_id,a.dev_type_id,ycl.MEASURETAG MEASURETAG,DATE_ADD(CURDATE(),INTERVAL -2 day) rtime,a.substation_Id bdzId,
						 round(avg(ycl.value),5) avg_data,
						 round(min(ycl.value),5) min_data,
						 round(max(ycl.value),5) max_data 
				from (select point.valid_flag,dev.substation_Id,dev.class_name dev_type_id,dev.id device_id,point.dev_id locate_zyid,b.measuretag from cisp_dev.v_all_dev dev,cisp_platform.me_conf_measurepoint point,cisp_platform.me_conf_measure b  where dev.id = point.END_DEV_ID and point.id = b.MEASUREPOINT_ID) a,
				measuredata.me_ycl_temp ycl
				where ycl.MEASURETAG = a.MEASURETAG  and ycl.DEV_ID = a.locate_zyid 
						 and ycl.SAMPLETIME >= DATE_ADD(CURDATE(),INTERVAL -1 day) and ycl.SAMPLETIME < CURDATE()
						 and (ycl.status <> 17 or ycl.status is null)
						 and ycl.MEASURETAG <>1 and ycl.value>-12345 and ycl.value is not null
						 and a.valid_flag = 1
				GROUP BY MEASURETAG,bdzId,device_id,a.dev_type_id
		) tj LEFT JOIN measuredata.tf_data_his lx on 
		lx.bdz_id=tj.bdzId and lx.device_type_id = tj.dev_type_id and lx.Device_id=tj.device_id and lx.MEASURETAG=tj.MEASURETAG and lx.rtime=tj.rtime where lx.bdz_id is null;



	-- 状态判断
	call judge_status_lxnh();

	-- 计算devicestatus并插入tf_dataa_his表
	update measuredata.tf_data_his a join 
	(
		select tj.data_flag,tj.Device_id,tj.rtime  
			from (
							select Device_id,rtime,case aa.data_flag when 2000 then 1 when 1000 then 2 when 999 then 3 when 998 then 4 when 0 then 0 end data_flag 
							from(
								select Device_id,rtime,max(case data_flag when 1 then 2000 when 2 then 1000 when  3 then  999 when 4 then 998  else 0 end ) data_flag 
								from measuredata.tf_data_his	
								GROUP BY Device_id,rtime
							) aa
					 ) tj LEFT JOIN  measuredata.tf_data_his his on tj.device_id = his.device_id and tj.rtime = his.rtime

	) cc on a.device_id = cc.device_id and a.rtime = cc.rtime
	set a.deviceStatus = cc.data_flag;




-- 提交事务
commit;


END */$$
